import React from "react";
import LocationItem from "../LocationItem/LocationItem";
import "./Locations.scss";

export default function Locations({ data }) {
  const renderAllLocations = () => {
    return data.slice(6).map((locations) => {
      return <LocationItem key={locations.id} {...locations} />;
    });
  };
  const filterLocations = (category) => {
    if (category === "All") {
      return data.slice(6).map((locations) => {
        return <LocationItem key={locations.id} {...locations} />;
      });
    } else if (category === "Adventorious") {
      return data
        .filter((label) => label.includes(category))
        .map((locations) => {
          return <LocationItem key={locations.id} {...locations} />;
        });
    }
  };
  return (
    <div className="places">
      <h1 className="places__title">Check out all the locations here</h1>
      <button value={"All"} onClick={(e) => filterLocations(e)}>
        All
      </button>
      <button value={"Adventurous"} onClick={(e) => filterLocations(e)}>
        Adventurous
      </button>
      <button value={"Mysterious"} onClick={(e) => filterLocations(e)}>
        Mysterious
      </button>
      <button value={"Luxurious"} onClick={(e) => filterLocations(e)}>
        Luxurious
      </button>
      <button value={"Fascinating"} onClick={(e) => filterLocations(e)}>
        Fascinating
      </button>
      <div className="places__container">
        <div className="places__wrapper">
          <div className="places__locations">{renderAllLocations()}</div>
        </div>
      </div>
    </div>
  );
}
