import React, { useState } from "react";
import "../../App.css";
import Footer from "../../components/Footer/Footer";
import NavBar from "../../components/NavBar/NavBar";
import "./ServicesPage.scss";
import { useNavigate } from "react-router-dom";
import Form from "../../components/Form/Form";

function ServicesPage() {
  const [image, setImage] = useState("");
  const [text, setText] = useState("");
  const [label, setLabel] = useState("adventurous");
  const [location, setLocation] = useState("");
  const [isPending, setIsPending] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    const imagePath = `../../assets/Images/${image}`;
    const newLocation = { imagePath, label, text, location };

    setIsPending("true");

    fetch("http://localhost:8000/blogs", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newLocation),
    }).then(() => {
      setIsPending("false");
      navigate("/locations");
      console.log("new place");
    });
  };

  return (
    <>
      <NavBar />
      <div className="services">
        <h1 className="services__title">Share your location</h1>
      </div>
      <div className="services__form-wrapper">
        <Form
          image={image}
          text={text}
          label={label}
          location={location}
          isPending={isPending}
        />
      </div>
      <Footer />
    </>
  );
}

export default ServicesPage;
