import React from "react";
import { useParams } from "react-router-dom";
import NavBar from "../../components/NavBar/NavBar";
import useFetch from "../../hooks/useFetch";

export default function PlacePage() {
  const { id } = useParams();

  const {
    data: place,
    error,
    isPending,
  } = useFetch("http://localhost:8000/blogs/" + id);

  const navigate = useNavigate();
  const handleClick = () => {
    fetch("http://localhost:8000/blogs/" + id, {
      method: "DELETE",
    }).then(() => {
      navigate("/home");
    });
  };
  return (
    <>
      <NavBar />
      <div>
        {isPending && <div>Loading...</div>}
        {error && <div>{error}</div>}
        {place && (
          <>
            <div>
              <img src="" alt="" />
            </div>
            <article>
              <h1></h1>
              <div>
                <div>Max 8 people</div>
                <div>Moderate difficulty</div>
                <div>Beginner experience</div>
              </div>
              <div>Overview</div>
              <div>
                <div>
                  Share with your friends:{" "}
                  <Link
                    className="footer__social-icons facebook"
                    to="https://www.facebook.com"
                    target="_blank"
                    aria-label="Facebook"
                  >
                    <i className="fab fa-facebook-f"></i>
                  </Link>{" "}
                  <Link
                    className="footer__social-icons instagram"
                    to="https://www.facebook.com"
                    target="_blank"
                    aria-label="Instagram"
                  >
                    <i className="fab fa-instagram"></i>
                  </Link>
                  <Link
                    className="footer__social-icons linkedin"
                    to="https://www.facebook.com"
                    target="_blank"
                    aria-label="LinkedIn"
                  >
                    <i className="fab fa-linkedin"></i>
                  </Link>
                </div>
                <button onClick={handleClick} className="location__button">
                  Delete Location
                </button>
              </div>
            </article>
          </>
        )}
      </div>
    </>
  );
}
