import React, { useState } from "react";
import LocationItem from "../LocationItem/LocationItem";
import "./Locations.scss";

export default function Locations({ data }) {
  const [filteredData, setFilteredData] = useState(data);
  const renderAllLocations = () => {
    return data.slice(6).map((locations) => {
      return <LocationItem key={locations.id} {...locations} />;
    });
  };
  const filterLocations = (value) => {
    const newData = data;
    console.log(value);
    if (value === "All") {
      return data.slice(6).map((locations) => {
        return <LocationItem key={locations.id} {...locations} />;
      });
    }
    if (value === "Adventurous") {
      const test = newData.filter((newVal) => {
        return newVal.label === value;
      });
      setFilteredData(test);
      console.log(test);
    }
  };

  return (
    <div className="places">
      <h1 className="places__title">Check out all the locations here</h1>
      <button value={"All"} onClick={(e) => filterLocations(e.target.value)}>
        All
      </button>
      <button
        value={"Adventurous"}
        onClick={(e) => filterLocations(e.target.value)}
      >
        Adventurous
      </button>
      <button
        value={"Mysterious"}
        onClick={(e) => filterLocations(e.target.value)}
      >
        Mysterious
      </button>
      <button
        value={"Luxurious"}
        onClick={(e) => filterLocations(e.target.value)}
      >
        Luxurious
      </button>
      <button
        value={"Fascinating"}
        onClick={(e) => filterLocations(e.target.value)}
      >
        Fascinating
      </button>
      <div className="places__container">
        <div className="places__wrapper">
          <div className="places__locations">{filterLocations()}</div>
        </div>
      </div>
    </div>
  );
}
